mapper.update:
update cloud_disk_files t
	set t.targe_name=#{targeName} 
	where t.own_user=#{ownUser} 
	and t.id=#{id};

update cloud_disk_files t
	set t.is_del=1 
	where t.own_user=#{ownUser} 
	and t.id=#{id};

mapper.select:
select * from cloud_disk_files t
	where t.own_user=#{ownUser}
	and t.is_del=0
	and t.file_type=#{fileType}
limit #{offset}, #{limit};

select count(1) from cloud_disk_files t
	where t.own_user=#{ownUser}
	and t.is_del=0;

mapper.delete:
    update cloud_disk_files t
		set t.is_del=1 where t.id in 

    	#{id}
