mapper.update:
update sop_project p 
	set p.project_status='projectStatus:2' 
	where p.id=#{projectId};

	update sop_project p

	   p.project_progress = #{projectProgress}, 
       p.progress_history = #{progressHistory} 

	where p.id=#{projectId};

	update sop_task 

	   task_status = #{taskStatus}, 
       updated_time = #{updatedTime} 

	where project_id=#{projectId} and task_flag=#{taskFlag};

mapper.select:
SELECT tt.id, tt.fileName, tt.fileSuffix, tt.fileKey, tt.fileValid, tt.fileTime, tt.fileDs, tt.fileWorktype, tt.fileWorktypeId FROM( SELECT f.id, concat(f.file_name,'.',f.file_suffix) as fileName, f.file_suffix fileSuffix, f.file_key fileKey, f.file_valid fileValid, f.updated_time fileTime, f.remark fileDs, dct1.name fileWorktype, dct1.dict_value fileWorktypeId FROM sop_file f LEFT JOIN dict dct1 ON dct1.parent_code='fileWorktype' and dct1.dict_code=f.file_worktype WHERE f.project_id=#{projectId}
and f.file_worktype in
#{fileWorkType}
ORDER BY f.updated_time DESC ) tt GROUP BY tt.fileWorktype;

mapper.update:
update sop_file
project_id = #{projectId}, belong_uid = #{belongUid}, file_valid = #{fileValid}, project_progress = #{projectProgress}, file_worktype = #{fileWorkType}, career_line = #{careerLine}, file_source = #{fileSource}, file_type = #{fileType}, remark = #{remark}, voucher_id = #{voucherId}, file_status = #{fileStatus}, file_uid = #{fileUid}, fil_uri = #{filUri}, updated_time = #{updatedTime}, created_time = #{createdTime}, file_length = #{fileLength}, file_key = #{fileKey}, bucket_name = #{bucketName}, file_name = #{fileName}, file_suffix = #{fileSuffix}, record_type = #{recordType}, meeting_id = #{meetingId},
where id = #{id};
