mapper.update:
update sop_project p 
	set p.project_status='projectStatus:2' 
	where p.id=#{projectId} and p.project_progress = #{projectProgress};

	update sop_project p
	
	   p.project_progress = #{projectProgress}, 
	   p.project_status = #{projectStatus}, 
       p.progress_history = #{progressHistory} 
	
	where p.project_status <> 'projectStatus:2' and p.id=#{projectId};

	update sop_task 
	
	   task_status = #{taskStatus}, 
       updated_time = #{updatedTime}, 
       task_deadline = #{taskDeadline} 
	
	where project_id=#{projectId} and task_flag=#{taskFlag} and task_type=#{taskType};

mapper.select:
SELECT tt.id, tt.fileName, tt.fileSuffix, tt.fileKey, tt.fileValid, tt.fileTime, tt.fileDs, tt.fileStatus, tt.fileStatusCode, tt.fileWorktype, tt.fileWorktypeId FROM( SELECT f.id, concat(f.file_name,'.',f.file_suffix) as fileName, f.file_suffix fileSuffix, f.file_key fileKey, f.file_valid fileValid, f.created_time fileTime, f.remark fileDs, dct2.name fileStatus, dct2.dict_code fileStatusCode, dct1.name fileWorktype, dct1.dict_value fileWorktypeId FROM sop_file f LEFT JOIN dict dct1 ON dct1.parent_code='fileWorktype' and dct1.dict_code=f.file_worktype LEFT JOIN dict dct2 ON dct2.parent_code='fileStatus' AND dct2.dict_code=f.file_status WHERE f.project_id=#{projectId} AND f.file_valid=1
and f.file_worktype in
#{fileWorkType}
ORDER BY f.created_time DESC ) tt GROUP BY tt.fileWorktype;

mapper.update:
update sop_file
project_id = #{projectId}, belong_uid = #{belongUid}, file_valid = #{fileValid}, project_progress = #{projectProgress}, file_worktype = #{fileWorkType}, career_line = #{careerLine}, file_source = #{fileSource}, file_type = #{fileType}, remark = #{remark}, voucher_id = #{voucherId}, file_status = #{fileStatus}, file_uid = #{fileUid}, fil_uri = #{filUri}, updated_time = #{updatedTime}, created_time = #{createdTime}, file_length = #{fileLength}, file_key = #{fileKey}, bucket_name = #{bucketName}, file_name = #{fileName}, file_suffix = #{fileSuffix}, record_type = #{recordType}, meeting_id = #{meetingId},
where id = #{id};

mapper.select:
SELECT tt.id,tt.task_name taskName,tt.task_flag taskFlag ,
	   tt.assign_uid assignUid,tt.name taskStatus ,tt.dict_code taskStatusCode 
FROM (
	    SELECT s.id,s.task_name,s.task_flag,s.assign_uid,dct1.name,dct1.dict_code FROM sop_task s
	    LEFT JOIN dict dct1 ON dct1.parent_code='taskStatus' and dct1.dict_code=s.task_status 
	    WHERE s.project_id=#{projectId}
		
			and s.task_flag in 
			
				#{taskFlag}
			
		
		ORDER BY s.updated_time DESC )tt
GROUP BY taskFlag;

SELECT r.id,r.title_id titleId,r.content_choose contentChoose,r.content_describe1 contentDescribe1
FROM information_title_relate tr
LEFT JOIN information_result r on r.title_id=tr.title_id
WHERE  
tr.report_type=4 
 and r.project_id = #{projectId} 
 and tr.parent_id = #{parentId} 
 and r.title_id = #{titleId}

mapper.update:
update information_result
project_id = #{projectId}, title_id = #{titleId}, content_choose = #{contentChoose}, content_describe1 = #{contentDescribe1}, content_describe2 = #{contentDescribe2}, sort = #{sort}, updated_time = #{updatedTime}, update_id = #{updateId}
where id = #{id};

mapper.select:
SELECT r.id, date_format(r.meeting_date,'%Y-%m-%d %H:%i') meetingDate, d1.name meetingResult, d1.dict_code meetingResultCode, r.meeting_type meetingType FROM sop_meeting_record r left join dict d1 on r.meeting_result=d1.dict_code WHERE r.project_id=#{projectId} and r.meeting_type=#{meetingType} and r.meet_valid = 0
and r.meeting_result in
#{res}
ORDER BY r.meeting_date DESC LIMIT 1;

SELECT f.id, f.project_id projectId, f.project_progress projectProgress, d1.name progress, f.file_suffix fileSuffix, f.file_key fileKey, f.bucket_name bucketName, f.file_name fileName, f.file_length fileLength, f.fil_uri filUri, f.file_type fileType, d2.name fType, f.file_worktype fileWorktype, d3.name fWorktype, f.file_status fileStatus, d4.name fileStatusDesc, f.file_source fileSource, case f.file_source WHEN 1 THEN '内部' WHEN 2 THEN '外部' else '' END fSource, f.record_type recordType, f.created_time createdTime, f.updated_time updatedTime, f.belong_uid belongUid, f.file_uid fileUid, f.career_line careerLine, u.user_name fileUName, d.name careerLineName FROM ( SELECT * FROM sop_file WHERE project_id =#{projectId} and file_valid=1
and file_worktype in
#{fileList}
ORDER BY created_time desc ) f LEFT JOIN dict d1 on f.project_progress=d1.dict_code LEFT JOIN dict d2 on f.file_type=d2.dict_code LEFT JOIN dict d3 on f.file_worktype=d3.dict_code LEFT JOIN dict d4 on f.file_status=d4.dict_code LEFT JOIN v_user u on u.id=f.file_uid LEFT JOIN v_department d on f.career_line=d.id GROUP BY f.file_worktype;
