mapper.update:
	update sop_meeting_scheduling set
		 schedule_status = #{scheduleStatus},
		updated_time = #{updatedTime}
	where  project_id = #{projectId} and meeting_type='meetingType:4';

mapper.select:
SELECT p.id, p.project_name projectName, sr.meeting_result results  FROM sop_project p 
LEFT JOIN sop_meeting_record sr on sr.project_id=p.id 
WHERE p.project_progress='projectProgress:11' and p.id=#{projectId} ;
