mapper.update:
	UPDATE sop_meeting_scheduling 
	
       status = #{status}, 
       schedule_status = #{scheduleStatus}, 
       meeting_count = #{meetingCount}, 
       updated_time = #{updateTime} 
	
	WHERE project_id=#{projectId} and meeting_type=#{meetingType};

mapper.select:
SELECT COUNT(1) 
FROM sop_meeting_record 
WHERE project_id=#{projectId} and meeting_type=#{meetingType} ;
