mapper.select:
SELECT tt.id, date_format(tt.meeting_date,'%Y/%m/%d %H:%i') meetingDateStr, tt.meeting_notes meetingNotes, dct1.dict_value meetingResultId, dct1.`name` meetingResult, dct2.dict_value resultReasonId, dct2.`name` resultReason, dct3.dict_value meetingTypeId, dct3.`name` meetingType, tt.reason_other reasonOther FROM( SELECT r.id, case r.meeting_type WHEN 'meetingType:3' THEN 'meeting3Result' WHEN 'meetingType:5' THEN 'meeting5Result' WHEN 'meetingType:1' THEN 'meeting1Result' WHEN 'meetingType:4' THEN 'meeting4Result' else 'meetingResult' end meetingResult, case WHEN locate('Veto',r.result_reason)>0 THEN 'meetingVetoReason' WHEN locate('Undetermined',r.result_reason)>0 THEN 'meetingUndeterminedReason' WHEN locate('Following',r.result_reason)>0 THEN 'meetingFollowingReason' else '' end resultReason, r.meeting_result, r.result_reason, r.meeting_date, r.meeting_notes, case WHEN locate('pos',r.meeting_type)>0 THEN 'postMeetingType' else 'meetingType' end meetingType, r.meeting_type, r.reason_other FROM sop_meeting_record r WHERE r.project_id=#{projectId}
and r.meeting_type in
#{meetingType}
and r.record_type=#{recordType})tt LEFT JOIN dict dct1 ON dct1.parent_code=tt.meetingResult and dct1.dict_code=tt.meeting_result LEFT JOIN dict dct2 ON dct2.parent_code=tt.resultReason and dct2.dict_code=tt.result_reason LEFT JOIN dict dct3 ON dct3.parent_code=tt.meetingType and dct3.dict_code=tt.meeting_type ORDER BY tt.meeting_date ${direction} LIMIT #{offset},${limit};

SELECT count(1) FROM( SELECT r.id, case r.meeting_type WHEN 'meetingType:3' THEN 'meeting3Result' WHEN 'meetingType:5' THEN 'meeting5Result' WHEN 'meetingType:1' THEN 'meeting1Result' WHEN 'meetingType:4' THEN 'meeting4Result' else 'meetingResult' end meetingResult, case WHEN locate('Veto',r.result_reason)>0 THEN 'meetingVetoReason' WHEN locate('Undetermined',r.result_reason)>0 THEN 'meetingUndeterminedReason' WHEN locate('Following',r.result_reason)>0 THEN 'meetingFollowingReason' else '' end resultReason, r.meeting_result, r.result_reason, r.meeting_date, r.meeting_notes, case WHEN locate('pos',r.meeting_type)>0 THEN 'postMeetingType' else 'meetingType' end meetingType, r.meeting_type, r.reason_other FROM sop_meeting_record r WHERE r.project_id=#{projectId}
and r.meeting_type in
#{meetingType}
and r.record_type=#{recordType})tt LEFT JOIN dict dct1 ON dct1.parent_code=tt.meetingResult and dct1.dict_code=tt.meeting_result LEFT JOIN dict dct2 ON dct2.parent_code=tt.resultReason and dct2.dict_code=tt.result_reason LEFT JOIN dict dct3 ON dct3.parent_code=tt.meetingType and dct3.dict_code=tt.meeting_type;

mapper.update:
update sop_meeting_record
file_id = #{fileId}, meeting_date = #{meetingDate}, meeting_notes = #{meetingNotes}, meeting_notes_text = #{meetingNotesText}, meeting_result = #{meetingResult}, result_reason = #{resultReason}, reason_other = #{reasonOther}, update_time = #{updatedTime}
where id=#{id};

mapper.select:
SELECT id, project_id projectId, health_state healthState, user_name userName
FROM sop_project_health 
WHERE project_id=#{projectId} 
ORDER BY created_time desc LIMIT 1;
