mapper.select:
select s.id, s.project_id projectId, s.meeting_type meetingType, d.name meetingTypeStr, s.meeting_count meetingCount, s.meeting_date meetingDate, s.status status, s.remark remark, s.schedule_status scheduleStatus, s.apply_time applyTime, date_format(s.apply_time,'%Y-%m-%d %H:%i:%s') applyTimeStr, s.reserve_time_start reserveTimeStart, s.reserve_time_end reserveTimeEnd, s.last_time lastTime, s. updated_time updatedTime, s.created_time createdTime, p.project_code projectCode, p.project_name projectName, p.create_uname createUname from sop_meeting_scheduling s LEFT JOIN sop_project p on s.project_id=p.id and p.is_delete=0 LEFT JOIN dict d on s.meeting_type=d.dict_code WHERE s.is_delete=0 and s.project_id in ( SELECT id FROM sop_project
is_delete=0 and create_uid = #{uid} and project_departId = #{projectDepartid}
and (upper(project_name) like CONCAT("%",upper(#{keyword}),"%")
escape '/'
or upper(project_code) like CONCAT("%",upper(#{keyword}),"%")
escape '/'
or project_name like CONCAT("%",#{ceeword},"%")
escape '/'
)
) and s.schedule_status = #{scheduleStatus} and s.meeting_type = #{meetingType}
and s.reserve_time_start >= STR_TO_DATE(CONCAT(#{startTime} , ' 00:00:00') ,'%Y-%m-%d %H:%i:%s')
order by ${sorting} order by meeting_count DESC,meeting_date DESC
limit #{offset}, #{limit}

select count(1)
from sop_meeting_scheduling s
LEFT JOIN sop_project p on s.project_id=p.id and p.is_delete=0
WHERE s.is_delete=0 and s.project_id in
	( SELECT id 
	  FROM sop_project 
	  
		
			is_delete=0
			 and create_uid = #{uid} 
		     and project_departId = #{projectDepartid} 
		    
			
				
				and (upper(project_name) like CONCAT("%",upper(#{keyword}),"%")
				
					escape '/'
				
				or upper(project_code) like CONCAT("%",upper(#{keyword}),"%")
				
					escape '/'
				
				or project_name like CONCAT("%",#{ceeword},"%")
				
					escape '/'
				
				)
			
		
	  
	)
and s.schedule_status = #{scheduleStatus} 
and s.meeting_type = #{meetingType} 
				
 	and s.reserve_time_start >= STR_TO_DATE(CONCAT(#{startTime} , ' 00:00:00') ,'%Y-%m-%d %H:%i:%s')

mapper.update:
update sop_meeting_scheduling

	is_delete = #{isDelete}, 
 	updated_time = #{updatedTime} 


	
		and project_id = #{projectId}
