where.trim:
	is_delete=0
    
	 and project_status = #{projectStatus} 
	 and project_departId = #{projectDepartid} 
	
	
		and project_status in 
		
			#{projectStatus}
		
	
	
		and id in 
		
			#{id}
		
	
	
		and create_uid in 
		
			#{tzjl}
		
		
	
		and project_type in 
		
			#{type}
		
		
	
		and project_progress in 
		
			#{progress}
		
	
	
		AND EXISTS(
		SELECT 1
		FROM information_title_relate t
		INNER JOIN information_result r ON t.title_id = r.title_id 
		WHERE r.project_id = sop_project.id AND t.code = 'FNO1_1' and t.report_type=4
		AND r.content_choose in  
		
			#{status}
		
		)
	
	
		AND EXISTS(
		SELECT 1 
		FROM information_result rst 
		WHERE rst.title_id = 1120 AND rst.project_id = sop_project.id 
		AND rst.content_choose in
		
			#{source}
		
		)
	
	
	
	
		and (project_name like CONCAT("%",#{keyword},"%")
		
			escape '/'
		
		or project_code like CONCAT("%",#{keyword},"%")
		
			escape '/'
		
		)

mapper.select:
SELECT ld.project_id projectId FROM information_listdata ld LEFT JOIN information_listdata_remark lr ON ld.title_id=lr.title_id LEFT JOIN v_user u ON ld.field_1=u.id LEFT JOIN v_rel_dep_user du ON u.id=du.user_id LEFT JOIN v_department d ON du.dep_id=d.id WHERE ld.field_5=1 AND lr.code='team-person'
and u.id in
#{userId}
and d.id = #{projectDepartid}
