mapper.update:
update schedule_info set 
	is_del=1,
	updated_time=#{updatedTime},
	updated_id=#{updatedId}
	where id=#{id};

sql.where:
t.isDel=0 and t.startTimeUnix>UNIX_TIMESTAMP(#{startTimeUnix}) and t.startTimeUnix<UNIX_TIMESTAMP(#{endTimeUnix}) and t.currentTimeUnix<=t.startTimeUnix and t.currentTimeUnix>t.startTimeUnix and t.significance=#{significance}
and t.isInterview=#{isInterview}
and (t.contactName like #{selectKey} or t.projectName like #{selectKey})

mapper.select:
select id, name
from schedule_info

	
		 and id != #{id} 
		 and created_id = #{userId} 
		and is_del = #{isDel}
	    and (start_time <= #{bqEndTime} and end_time >= #{eqStartTime} and is_allday = 0 
		or (start_time >= #{sbTimeForAllday} and start_time <= #{seTimeForAllday} and is_allday = 1))

select
id, name
from schedule_info

	
		 and id != #{idIsNotEq} 
		 and created_id = #{createdId} 
		 and start_time >= #{bqStartTime} 
		 and start_time <= #{bqEndTime} 
		 and end_time >= #{eqStartTime} 
		 and is_del = #{isDel}  
		 and is_allday = #{isAllday}

trim.if:
     and (start_time <= #{bqEndTime} 
      and end_time >= #{eqStartTime} 
	and is_allday = 0
	or (
		  start_time >= #{sbTimeForAllday} 
	      and start_time <= #{seTimeForAllday} 
		and is_allday = 1 
		)
	)

mapper.select:
select id id, type type, name_id nameId, name name, parent_id parentId, project_type projectType,project_id projectId, start_time startTime, end_time endTime, is_allday isAllDay, wakeup_id wakeupId, remark, created_id createdId, updated_id updatedId, created_time createdTime, updated_time updatedTime, is_del isDel, callon_person callonPerson from schedule_info
and is_del = #{isDel} and created_id = #{createdId}
and ((date_format(#{date},'%y-%m') >= date_format(start_time,'%y-%m') AND date_format(end_time,'%y-%m') >= date_format(#{date},'%y-%m' ) and is_allday=0) or(date_format(#{date},'%y-%m') <= date_format(start_time,'%y-%m') AND date_format(start_time,'%y-%m') >= date_format(#{date},'%y-%m' ) and is_allday=1 ))
and ((date_format(#{date},'%y-%m-%d') >= date_format(start_time,'%y-%m-%d') AND date_format(end_time,'%y-%m-%d') >= date_format(#{date},'%y-%m-%d') and is_allday=0) or(date_format(#{date},'%y-%m-%d') = date_format(start_time,'%y-%m-%d') and is_allday=1 ))
order by ${property} ${direction}

update sop_interview_record
set interview_valid=1,
update_time=#{updatedTime}
where id=#{id}

update sop_meeting_record
set meet_valid=1,
update_time=#{updatedTime}
where id=#{id}

select id,project_id projectId, file_name fileName from sop_file
where file_valid =1 and
interview_record_id=#{interviewRecordId}
meeting_record_id=#{meetinRecordId}

update sop_file
set file_valid=0,
updated_time=#{updatedTime}
where id=#{id}
