mapper.select:
SELECT p.id,p.project_name projectName FROM sop_project p WHERE p.id not in(SELECT tr.project_id FROM sop_transfer_record tr WHERE tr.record_status is null) and p.create_uid=#{createdId} and p.project_status = 'projectStatus:0' and p.project_progress='projectProgress:1'
and project_name like concat(concat('%','${pName}'),'%')
limit #{offset}, #{limit};

SELECT 

from sop_project

  project_name = #{projectName}
and id = #{id}

mapper.update:
update sop_project
project_name = #{projectName}, stock_transfer = #{stockTransfer}, project_careerline = #{projectCareerline}, project_departId = #{projectDepartId}, industry_own = #{industryOwn}, currency_unit = #{currencyUnit}, project_company = #{projectCompany}, formation_date = #{formationDate}, project_company_code = #{projectCompanyCode}, company_legal = #{companyLegal}, create_uid = #{createUid}, create_uname = #{createUname}, create_uposition = #{createUposition}, project_progress = #{projectProgress}, project_status = #{projectStatus}, updated_time = #{updatedTime}, created_time = #{createdTime}, project_describe = #{projectDescribe}, project_describe_financing = #{projectDescribeFinancing}, project_business_model = #{projectBusinessModel}, company_location = #{companyLocation}, user_portrait = #{userPortrait}, prospect_analysis = #{prospectAnalysis}, next_financing_source = #{nextFinancingSource}, industry_analysis = #{industryAnalysis}, operational_data = #{operationalData}, fa_flag = #{faFlag} , fa_name = #{faName} , finance_mode = #{financeMode} , project_time = #{projectTime}
where id = #{id}

mapper.select:
SELECT id, project_id projectId,title_id titleId, content_choose contentChoose, content_describe1 contentDescribe1 
FROM information_result 
WHERE project_id=#{projectId}  
and title_id=#{titleId} and is_valid=0;

mapper.update:
update information_result
project_id = #{projectId}, title_id = #{titleId}, content_choose = #{contentChoose}, content_describe1 = #{contentDescribe1}, content_describe2 = #{contentDescribe2}, sort = #{sort}, updated_time = #{updatedTime}, update_id = #{updateId}
where id = #{id};
