mapper.select:
SELECT tt.id, date_format(tt.view_date,'%Y/%m/%d %H:%i') viewDateStr, case tt.schedule_id WHEN 0 THEN tt.view_target else tt.name end viewTarget, tt.project_id projectId, tt.project_name projectName FROM( SELECT r.id, r.view_date, con.name, r.view_target, r.schedule_id, r.project_id, p.project_name FROM sop_interview_record r LEFT JOIN schedule_contacts con on r.view_target=con.id LEFT JOIN sop_project p ON p.id = r.project_id WHERE 1=1 and r.interview_valid = 0
and r.project_id=#{proId}
and r.schedule_id in (SELECT id FROM schedule_info WHERE callon_person=#{conId})
and r.id<>#{talkRecordId}
ORDER BY r.view_date desc
limit #{offset}, #{limit}
) tt;

SELECT COUNT(1) FROM sop_interview_record WHERE 1=1 and interview_valid = 0

	
		and project_id=#{proId}
	
	
		and schedule_id in (SELECT id FROM schedule_info WHERE callon_person=#{conId})
	


	 and id<>#{talkRecordId};

mapper.update:
update sop_interview_record
interview_valid = #{interviewValid}, file_id = #{fileId}, view_target = #{viewTarget}, view_notes = #{viewNotes}, view_notes_text = #{viewNotesText}, interview_result = #{interviewResult}, result_reason = #{resultReason}, reason_other = #{reasonOther}, update_time = #{updatedTime}
where id=#{id};
